import { useState } from "react";

export default function App() {
  return (
    <div className="container">
      <LoginForm />
    </div>
  );
}

function LoginForm() {
  const [name, setName] = useState("");
  const [cardNumber, setCardNumber] = useState("");
  const [month, setMonth] = useState("");
  const [year, setYear] = useState("");
  const [cvc, setCVC] = useState("");
  const [cardDetails, setCardDetails] = useState({});

  const isEmpty = Object.keys(cardDetails).length === 0;

  console.log(cardDetails);

  function handleSubmit(e) {
    e.preventDefault();

    const credentials = {
      name,
      cardNumber,
      month,
      year,
      cvc,
    };

    setCardDetails(credentials);

    setName("");
    setCardNumber("");
    setMonth("");
    setYear("");
    setCVC("");
  }

  return (
    <>
      <div className="image-box">
        <HoldersCard
          name={name}
          cardNumber={cardNumber}
          month={month}
          year={year}
          cvc={cvc}
          cardDetails={cardDetails}
          isEmpty={isEmpty}
        />
        <div className="back-card-box"></div>
      </div>
      <div className="form-details-box">
        <CardDetails
          name={name}
          cardNumber={cardNumber}
          month={month}
          year={year}
          cvc={cvc}
          cardDetails={cardDetails}
          isEmpty={isEmpty}
          onSetMonth={setMonth}
          onSetYear={setYear}
          onSetCVC={setCVC}
          onSetName={setName}
          onSetCardNumber={setCardNumber}
          onhandleSubmit={handleSubmit}
        />
      </div>
    </>
  );
}
function HoldersCard({ name, cardNumber, month, year, cardDetails, isEmpty }) {
  function formatMonth(month) {
    if (month < 10) return `0${month}`;
    else return month;
  }

  return (
    <div className="card-box">
      <div className="card-logo-box">
        <svg
          width="84"
          height="47"
          fill="none"
          xmlns="http://www.w3.org/2000/svg"
        >
          <ellipse cx="23.478" cy="23.5" rx="23.478" ry="23.5" fill="#fff" />
          <path
            d="M83.5 23.5c0 5.565-4.507 10.075-10.065 10.075-5.559 0-10.065-4.51-10.065-10.075 0-5.565 4.506-10.075 10.065-10.075 5.558 0 10.065 4.51 10.065 10.075Z"
            stroke="#fff"
          />
        </svg>
      </div>
      <h2 className="card-number">
        {isEmpty
          ? cardNumber
            ? cardNumber
            : "0000 0000 0000 0000"
          : cardDetails.cardNumber}
      </h2>
      <div className="card-detail">
        <strong>
          {isEmpty ? (name ? name : "Jane Appleseed") : cardDetails.name}
        </strong>
        <strong>
          {isEmpty
            ? month
              ? formatMonth(month)
              : "00"
            : formatMonth(cardDetails.month)}
          /{isEmpty ? (year ? year : "00") : cardDetails.year}
        </strong>
      </div>
    </div>
  );
}

function CardDetails({
  name,
  cardNumber,
  month,
  year,
  cvc,
  cardDetails,
  isEmpty,
  onSetMonth,
  onSetYear,
  onSetCVC,
  onSetName,
  onSetCardNumber,
  onhandleSubmit,
}) {
  function formatCreaditCardNumber(e) {
    const input = e.target;
    const trimmedValue = input.value.replace(/\s+/g, "");
    const formattedValue = trimmedValue.replace(/(\d{4})/g, "$1 ").trim();
    onSetCardNumber(formattedValue.length > 19 ? cardNumber : formattedValue);
  }

  // function handleFocus(e) {
  // }

  return (
    <>
      <form className="card-details" onSubmit={onhandleSubmit}>
        <div className="form-element">
          <label>Cardholder Name</label>
          <Input
            type={"text"}
            value={name}
            onChange={(e) => onSetName(e.target.value)}
            placeholder={"e.g Jane Appleseed"}
          />
          {!cardDetails?.name && (
            <span className="invalid-text">Please enter your name</span>
          )}
        </div>

        <div className="form-element">
          <label>Card Number</label>
          <Input
            type={"text"}
            value={cardNumber}
            onChange={(e) => formatCreaditCardNumber(e)}
            placeholder={"e.g. 9591 6489 6389 101E"}
          />
          {!cardDetails?.cardNumber && (
            <span className="invalid-text">Please enter your card number</span>
          )}
        </div>

        <div className="form-element form-element--date">
          <div>
            <label>exp. date (MM/YY)</label>
            <div className="exp-date">
              <Input
                type={"number"}
                value={month}
                onChange={(e) => onSetMonth(Number(e.target.value))}
                placeholder={"MM"}
              />

              <Input
                type={"number"}
                value={year}
                onChange={(e) => onSetYear(Number(e.target.value))}
                placeholder={"YY"}
              />
            </div>
            <span></span>
          </div>

          <div>
            <label>cvc</label>
            <Input
              type={"number"}
              value={cvc}
              onChange={(e) => onSetCVC(Number(e.target.value))}
              placeholder={"e.g. 123"}
            />
            <span></span>
          </div>
        </div>
        <Button>Confirm</Button>
      </form>
    </>
  );
}

function Input(props) {
  const [isFocused, setIsFocused] = useState(false);

  return (
    <input
      className={isFocused ? (!props.value ? "invalid" : "valid") : ""}
      type={props.type}
      value={props.value}
      onChange={props.onChange}
      onFocus={() => setIsFocused(true)}
      onBlur={() => setIsFocused(false)}
      placeholder={props.placeholder}
    />
  );
}

// function InvalidText({ cardDetails, children }) {
//   return (
//     <>
//       {cardDetails?.name === "" && (
//         <span className="invalid-text">{children}</span>
//       )}
//     </>
//   );
// }

function Button({ children }) {
  return <button className="btn">{children}</button>;
}
